package com.tnsif.basicsdaytwo;

public class ContinueDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
				
		for(int k=1;k<15;k++)
		{
			//Odd number are skipped
			if(k%2!=0)
			{
			     continue;
			//Even are printed
			     
			}
			System.out.println(k);
		}
	}

}
